svg.script:
var svgDocument;

function startup(evt)  {
    svgDocument=evt.target.ownerDocument ;
    cockpit = svgDocument.getElementById("cockpit");
    monitor1 = svgDocument.getElementById("monitor1");
    monitor = svgDocument.getElementById("monitor");
    crosshairs1 = svgDocument.getElementById("crosshairs1");
    crosshairs2 = svgDocument.getElementById("crosshairs2");
    crosshairs3 = svgDocument.getElementById("crosshairs3");
    crosshairs4 = svgDocument.getElementById("crosshairs4");
    planet = svgDocument.getElementById("planet"); 
    text1 = svgDocument.getElementById("text1");
    monitor2 = svgDocument.getElementById("monitor2");
    keyPress = svgDocument.getElementById("keyPress");
    keyPressVisible = false;
    width = -200;    
    moveMonitorStep = 0;
    openCrosshairsStep = 0;
    moveCockpit();
}

function moveCockpit() {
    cockpit.setAttribute ("transform", "translate("+width+",0)"); 
    width++;  
    if(width < 0) {
        window.setTimeout("moveCockpit()",10);
    } else {
         window.setTimeout("showMonitor()",500);
    }
}

//------------------------------------------------------------------------------------------------------------

function showMonitor() {
    monitor.setAttribute ("style", "fill:#bcbdff;fill-opacity:0.2");
    window.setTimeout("showCrosshairs()",200); 
}

function showCrosshairs() {
    crosshairs1.setAttribute ("style", "fill:none;stroke:#ff0000;stroke-width:1px;stroke-linecap:butt;stroke-linejoin:miter;stroke-opacity:1");
    crosshairs2.setAttribute ("style", "fill:none;stroke:#ff0000;stroke-width:1px;stroke-linecap:butt;stroke-linejoin:miter;stroke-opacity:1");     
    crosshairs3.setAttribute ("style", "fill:none;stroke:#ff0000;stroke-width:1px;stroke-linecap:butt;stroke-linejoin:miter;stroke-opacity:1");
    crosshairs4.setAttribute ("style", "fill:none;stroke:#ff0000;stroke-width:1px;stroke-linecap:butt;stroke-linejoin:miter;stroke-opacity:1");

    planet.setAttribute ("style", "opacity:1");

    window.setTimeout("openCrosshairs()",200); 
}

function openCrosshairs() {
    setCrosshairsSize(0, 0, openCrosshairsStep);

    openCrosshairsStep++;
    if(openCrosshairsStep < 16) {
        window.setTimeout("openCrosshairs()", 10); 
    } else {
        window.setTimeout("closeCrosshairs()", 10); 
    }
}

function closeCrosshairs() {
    setCrosshairsSize(0, 0, openCrosshairsStep);

    openCrosshairsStep--;
    if(openCrosshairsStep > 0) {
        window.setTimeout("closeCrosshairs()", 10); 
    } else {
         moveCrosshairs();
         text1.setAttribute ("style", "opacity:1");
         window.setTimeout("moveMonitor()", 1000); 
    }
}

function moveCrosshairs() {
    setCrosshairsSize(10 - 20 * Math.random(), 10 - 20 * Math.random(), 0);
    window.setTimeout("moveCrosshairs()", 500); 
}

function setCrosshairsSize(positionX, positionY, size) {
    crosshairs1.setAttribute ("transform", "translate(" + (positionX - size) + ", " + (positionY - size) + ")");
    crosshairs2.setAttribute ("transform", "translate(" + (positionX - size) + ", " + (positionY +size) + ")");
    crosshairs3.setAttribute ("transform", "translate(" + (positionX + size) + ", " + (positionY - size) + ")");
    crosshairs4.setAttribute ("transform", "translate(" + (positionX + size) + ", " + (positionY + size) + ")");
}

//------------------------------------------------------------------------------------------------------------

function moveMonitor() {
     monitor1.setAttribute ("transform", "translate(0, " + moveMonitorStep + ")");
     moveMonitorStep++;
     if(moveMonitorStep < 160) {
        window.setTimeout("moveMonitor()", 10); 
     } else {
         showMonitor2();
     }
}

function showMonitor2() {
    monitor2.setAttribute ("style", "opacity:1");
    window.setTimeout("showKeyPress()", 1000); 
}

function showKeyPress() {
    if(keyPressVisible) {
        keyPress.setAttribute ("style", "opacity:0");
        window.setTimeout("showKeyPress()", Math.random() * 1000);
        keyPressVisible = false;
    } else {
        keyPress.setAttribute ("style", "opacity:1");
        window.setTimeout("showKeyPress()", Math.random() * 200); 
        keyPressVisible = true;
    }
}
